INSERT INTO `clip` VALUES ('1508', '20150921101444191_6', '1', 'Phượng Vũ khóc nức nở trong ngày ra mắt single đầu tay', null, 'Tình hoang phế, một sáng tác mới nhất của nhạc sĩ Trương Lê Sơn, được chính Đàm Vĩnh Hưng đặt hàng riêng cho Phượng vũ để quay MV. Phượng Vũ cho biết khi đọc phần lời ca khúc, cô đã bật khóc vì gần như chính cuộc đời và hoàn cảnh sống của mình được tái hiện lại. Cũng tại buổi họp báo, Đàm Vĩnh Hưng đã chia sẻ về mối quan hệ của anh với cô học trò đặc biệt này trong khi Phượng Vũ nức nở khóc vì quá hạnh phúc khi giấc mơ của mình đã thành hiện thực. (Theo TGVH)', 'Phượng Vũ, Đàm Vĩnh Hưng, Tình hoang phế', '163', '0', '2000', '1', '2', '199', '0', '0', '0', '0', '1', '1', '0', 'vn', '2015-09-22 09:08:02', '2015-09-22 09:08:23', '0', '0000-00-00 00:00:00', '0', '0', '0', '0', '1', null, '0', '0', '0', '160823', '0', '1', '21');
