INSERT INTO `clip` VALUES ('1521', '20150922140054129_43', '1', '\'\'Thần đồng\'\' 9 tuổi khiến cả làng nhạc EDM \'\'ngả mũ\'\'', null, 'Trong khi những đứa trẻ khác thậm chí không biết đến DJ hay EDM là gì nhưng John Anthony, một thần đồng DJ 9 tuổi lại có thể khuấy đảo được cả lễ hội EDM. Cậu bé người Florida chỉ học lớp 3 nhưng em có thể chơi được piano, violin, đánh golf, một cầu thủ bóng đá và đặc biệt là em còn làm DJ và cả producer. Cậu từng được chơi nhạc cùng các DJ tên tuổi khác như  Tiesto, Skrillex và Armin Van Buuren tại lễ hội Sunset Music Festival ở Tampa, Florida.', '\'\'Thần đồng\'\' 9 tuổi khiến cả làng nhạc EDM \'\'ngả mũ\'\'', '195', '0', '2000', '1', '2', '199', '0', '0', '0', '0', '1', '1', '0', 'vn', '2015-09-23 08:40:03', '2015-09-23 08:40:54', '0', '0000-00-00 00:00:00', '0', '0', '0', '0', '1', null, '0', '0', '0', '160991', '0', '1', '21');
