INSERT INTO `clip` VALUES ('1525', '20150922092627129_18', '1', '\'\'Đỏ mặt\'\' vì vòng 3 của Jennifer Lopez \'\'lộ thiên\'\' trên sân khấu', null, 'Mới đây, Jennifer Lopez đã xuất hiện tại liên hoan âm nhạc iHeartRadio diễn ra sôi động ở sân vận động MGM Grand Las Vegas. Nữ ca sĩ 46 tuổi lên sân khấu trong trang phục diễn màu trắng, thiết kế cut-out phóng khoáng khá giống với bộ đồ bikini khiến vòng 3 gần như lộ thiên trên sân khấu. Trước khi Kim Kardashian nổi tiếng, Jennifer được mệnh danh là ngôi sao có vòng 3 đẹp nhất thế giới.', '\'\'Đỏ mặt\'\' vì vòng 3 của Jennifer Lopez \'\'lộ thiên\'\' trên sân khấu', '97', '0', '2000', '1', '2', '199', '0', '0', '0', '0', '1', '1', '0', 'vn', '2015-09-23 08:40:03', '2015-09-23 08:40:45', '0', '0000-00-00 00:00:00', '0', '0', '0', '0', '1', null, '0', '0', '0', '160949', '0', '1', '21');
