INSERT INTO `clip` VALUES ('1609', '20170313105700154_79', '1', 'Tự làm kẹo trà sữa cực chất ăn chơi', null, 'Kẹo trà sữa hay kẹo sữa trà xanh là món ăn vặt thơm ngon, vị ngọt thanh thanh, vị béo ngậy của sữa cùng hương vị trà xanh hòa quện là món ăn được nhiều người yêu thích và ấn tượng khi ăn. Nếu bạn cũng đã từng một lần thử ăn kẹo trà sữa, chắc chắn bạn sẽ ao ước được thưởng thức nó thêm nhiều lần nữa. Dưới đây chúng tôi sẽ mách bạn công thức làm kẹo trà sữa vừa ngon, vừa đơn giản.', '[\"T\\u1ef1 l\\u00e0m k\\u1eb9o tr\\u00e0 s\\u1eefa c\\u1ef1c ch\\u1ea5t \\u0103n ch\\u01a1i\",\"\",\"Tu lam keo tra sua cuc chat an choi\"]', '47', '0', '2000', '20', '2', '199', '0', '0', '119', '239', '1', '0', '1', 'vn', '2017-03-29 17:09:47', '2017-04-11 10:59:06', '163', '2017-04-04 15:48:03', '163', '0', '0', '0', '1', null, '0', '0', '0', '202775', '0', '1', '21');
