INSERT INTO `clip` VALUES ('2113', '20170523100912154_40', '1', 'Muốn đổi món hãy thử làm chạo tôm bọc mía', null, 'Từng cây chạo tôm có màu vàng rượm, mang vị ngọt tự nhiên từ tôm tươi, ăn vào nghe cảm giác dai dai rất thích. Chạo tôm ăn kèm với bánh hỏi hoặc bún, kết hợp với các loại rau thơm, chấm với nước mắm tỏi ớt chua ngọt là đúng bài. Rưới lên những cây chạo mỡ hành nóng hổi, rắc thêm một ít đậu phộng rang và hành phi, thiệt là hấp dẫn khó mà có thể chối từ.', '[\"Mu\\u1ed1n \\u0111\\u1ed5i m\\u00f3n h\\u00e3y th\\u1eed l\\u00e0m ch\\u1ea1o t\\u00f4m b\\u1ecdc m\\u00eda\",\"\",\"Muon doi mon hay thu lam chao tom boc mia\"]', '65', '0', '0', '23', '2', '199', '0', '0', '113', '227', '1', '0', '1', 'vn', '2017-06-06 11:40:04', '2017-06-06 11:40:38', '0', '2017-06-27 17:09:41', '199', '0', '0', '0', '1', null, '1', '0', '0', '206519', '0', '1', '21');
