INSERT INTO `clip` VALUES ('2348', '20170628094217154_21', '1', 'Muốn sống sót, hãy học cách ăn côn trùng từ bây giờ!', null, 'Côn trùng là nguồn thức ăn chính của tương lai - đó là dự báo của các nhà khoa học. Họ còn khẳng định rằng trong 10 - 12 năm tới, côn trùng sẽ được bán phổ biến trong siêu thị như thịt lợn, và sẽ là nguồn thực phẩm chủ yếu thay thế cho nhiều loại thực phẩm khác. Nếu bạn còn sợ món ăn này, hãy học cách làm quen với chúng ngay từ bây giờ!', '[\"Mu\\u1ed1n s\\u1ed1ng s\\u00f3t, h\\u00e3y h\\u1ecdc c\\u00e1ch \\u0103n c\\u00f4n tr\\u00f9ng t\\u1eeb b\\u00e2y gi\\u1edd!\",\"\",\"Muon song sot, hay hoc cach an con trung tu bay gio!\"]', '96', '0', '0', '22', '2', '199', '0', '0', '59', '113', '1', '0', '1', 'vn', '2017-07-26 11:30:02', '2017-07-26 11:30:26', '0', '2017-07-31 14:07:46', '199', '0', '0', '0', '1', null, '1', '0', '0', '208520', '0', '1', '21');
INSERT INTO `clip` VALUES ('2351', '20170717105934154_130', '1', 'Lòng gà trứng non cháy tỏi ăn cực đã  ngày mưa', null, 'Lòng gà cháy tỏi với những cái trứng non vàng ươm, hương vị bùi bùi, lòng gà giòn sực đậm đà thơm lừng mùi bơ tỏi. Đây chính là món được các bạn trẻ săn lùng ráo riết suốt thời gian qua, nhìn thôi đã thấy đói bụng rồi. Những ngày trời mưa lạnh hay những bữa tiệc gia đình mà có đĩa lòng gà nóng hổi xì xèo, khói bốc nghi ngút này kế bên thì còn gì hấp dẫn hơn nữa đúng không.', 'lòng gà, món ngon, trứng non, cách làm, cháy tỏi', '58', '0', '0', '1', '2', '199', '0', '0', '0', '0', '0', '0', '0', 'vn', '2017-07-26 11:30:02', '2017-07-26 11:30:02', '0', '0000-00-00 00:00:00', '0', '0', '0', '0', '1', null, '0', '0', '0', '209399', '0', '0', '0');
